#include<stdio.h>int main(){    double sum;    int T,a[20],n,i;    scanf("%d",&T);    while(T--){        scanf("%d",&n);        if(n==0){            continue;        }        for(i=0;i<18;i++){            a[i]=0;        }        for(i=0;i<n;i++){            scanf("%d",&a[i]);        }        sum=1;        for(i=0;i<n;i++){            sum=sum*a[i];        }        printf("%.0lf\n",sum);    }    return 0;}